                    new HealthInformation("InternalWatchdog", "LogicProperty", HealthState.Ok)
                    {
                        Description = "Description of health report goes here.",
                        TimeToLive = TimeSpan.FromMinutes(60)
                    });

                // Report health
                try
                {
                    fabricClient.HealthManager.ReportHealth(applicationHealthReport);
