                    Console.WriteLine("Error: " + e.Message);

                    if (e.InnerException != null)
                        Console.WriteLine("  Inner Exception: " + e.InnerException.Message);

                    return false;
                }

                Console.WriteLine("Application health reported successfully.");
                return true;
            }

